var channelMap = {
      'bt-sport-1': {
            longName: 'BT Sport 1',
            url: 'https://sport.bt.com/btsportplayer/bt-sport-1-01363810201090'
      },
      'sky-sports-1': {
            longName: 'Sky Sports 1',
            url: 'http://www.skysports.com/watch/sky-sports-1'
      }
};
function fixturesForTeam(fixtures, team) {
	return _.filter(fixtures, function(f) {
		return f.home === team || f.away === team;
	});
}

function changeTeam(e) {
	team = localStorage.team = e.currentTarget.dataset.team;
	writeNextGame();
}

function writeTeams() {
	teams.forEach(function(t) {
		var li = document.createElement('li');
            var img = document.createElement('img');
            img.src = 'images/crests/' + t + '.png';
		li.onclick = changeTeam;
            li.className = 'team-crest';
            li.setAttribute('data-team', t);
            li.appendChild(img);
		teamCont.appendChild(li);
	});
}

function getBroadcastChannel(fixture) {
      var fixtureDate = new Date(fixture.date).toDateString();
      var gamesOnDate = _.find(broadcastData.dateList, function(o) {
            return new Date(o.date).toDateString() === fixtureDate;
      }); 
      //very rough match
      if (gamesOnDate) {
            var matchingGame = _.find(gamesOnDate.broadcastList, function (game) {
                  return game.showName.toLowerCase().indexOf(fixture.home) > -1;
            });
            if (matchingGame) {
                  return matchingGame.channel;
            }
      }
}

function writeNextGame() {
	var teamFixtures = fixturesForTeam(fixtures, team);
	var futureFixtures = teamFixtures.filter(function(f) {
		return f.date >= todayISO;
	});
	var nextFixture = futureFixtures[0];
      var channel = getBroadcastChannel(nextFixture);
	nextGameCont.querySelector('.next-game-date').innerHTML = new Date(nextFixture.date).toGMTString();
	nextGameCont.querySelector('.next-game-teams').innerHTML =
            "<img class='team-crest team-home' src='images/crests/" + nextFixture.home + ".png'></img>" +
            "<span class='vs'>vs</span>" +
            "<img class='team-crest team-away' src='images/crests/" + nextFixture.away + ".png'></img>";
      if (channel) {
            var channelDetails = channelMap[channel];
            var channelName = channelDetails && channelDetails.longName || channel;
            var channelUrl = channelDetails && channelDetails.url || '';
            nextGameCont.querySelector('.next-game-channel').innerHTML = "<a target='_blank' href='" + channelUrl + "'>" + channelName + "</a>";
      } else {
            nextGameCont.querySelector('.next-game-channel').innerHTML = ''; 
      }
}

function parseFixtures(json) {
	fixtures = json;
	teams = _(fixtures).pluck('home').uniq().sort().value();
	writeTeams();
	writeNextGame();
}

fetch('data/pl-fixtures.json').then(function(response) {
	return response.json()
}).then(parseFixtures)
.catch(function(ex) {
	console.error('parsing failed', ex)
});